import React, { Component } from "react";
import { Switch, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

import AuthService from "./services/auth.service";

import Login from "./components/login.component";
import Register from "./components/register.component";
import Home from "./components/home.component";
import Pricing from "./components/pricing.component";
import Profile from "./components/profile.component";
import BoardUser from "./components/board-user.component";
import BoardModerator from "./components/board-moderator.component";
import BoardAdmin from "./components/board-admin.component";
import ChestEx from "./components/chest-ex.component";
import BackEx from "./components/back-ex.component";
import ShoulderEx from "./components/shoulder-ex.component";
import BicepEx from "./components/bicep-ex.component";
import TricepEx from "./components/tricep-ex.component";
import LegEx from "./components/leg-ex.component";
import CoreEx from "./components/core-ex.component";
import StretchEx from "./components/stretch-ex.component";
import WarmUpEx from "./components/warmup-ex.component";
import ChestExVids from "./components/chest-ex-vids.component";
import BackExVids from "./components/back-ex-vids.component";
import ShoulderExVids from "./components/shoulder-ex-vids.component";
import BicepExVids from "./components/bicep-ex-vids.component";
import TricepExVids from "./components/tricep-ex-vids.component";
import LegExVids from "./components/leg-ex-vids.component";
import CoreExVids from "./components/core-ex-vids.component";
import StretchExVids from "./components/stretch-ex-vids.component";
import WarmUpExVids from "./components/warmup-ex-vids.component";

class App extends Component {
  constructor(props) {
    super(props);
    this.logOut = this.logOut.bind(this);

    this.state = {
      showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    };
  }

  componentDidMount() {
    const user = AuthService.getCurrentUser();

    if (user) {
      this.setState({
        currentUser: user,
        showModeratorBoard: user.roles.includes("ROLE_MODERATOR"),
        showAdminBoard: user.roles.includes("ROLE_ADMIN"),
      });
    }
  }

  logOut() {
    AuthService.logout();
  }

  render() {
    const { currentUser, showModeratorBoard, showAdminBoard } = this.state;

    return (
      <div>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">

          <Link to={"/"} className="navbar-brand">
            Off Strength Workouts
          </Link>

          <div className="navbar-nav mr-auto">


            {showModeratorBoard && (
              <li className="nav-item">
                <Link to={"/mod"} className="nav-link">
                  Moderator Board
                </Link>
              </li>
            )}

            {showAdminBoard && (
              <li className="nav-item">
                <Link to={"/admin"} className="nav-link">
                  Admin Board
                </Link>
              </li>
            )}

            {currentUser && (
              <li className="nav-item">
                <Link to={"/user"} className="nav-link">
                  User
                </Link>
              </li>
            )}
          </div>

          {currentUser ? (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/profile"} className="nav-link">
                  {currentUser.username}
                </Link>
              </li>
              <li className="nav-item">
                <a href="/login" className="nav-link" onClick={this.logOut}>
                  LogOut
                </a>
              </li>
            </div>
          ) : (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/login"} className="nav-link">
                  Login
                </Link>
              </li>

              <li className="nav-item">
                <Link to={"/register"} className="nav-link">
                  Sign Up
                </Link>
              </li>
              <li className="nav-item">
                <Link to={"/pricing"} className="nav-link">
                  Pricing
                </Link>
              </li>

            </div>
          )}
        </nav>

        <div className="container mt-3">
          <Switch>
            <Route exact path={["/", "/home"]} component={Home} />
            <Route exact path="/login" component={Login} />
            <Route exact path="/register" component={Register} />
            <Route exact path="/profile" component={Profile} />
            <Route exact path="/pricing" component={Pricing} />
            <Route path="/user" component={BoardUser} />
            <Route path="/chestex" component={ChestEx} />
            <Route path="/backex" component={BackEx} />
            <Route path="/shoulderex" component={ShoulderEx} />
            <Route path="/bicepex" component={BicepEx} />
            <Route path="/tricepex" component={TricepEx} />
            <Route path="/legex" component={LegEx} />
            <Route path="/coreex" component={CoreEx} />
            <Route path="/stretchex" component={StretchEx} />
            <Route path="/warmupex" component={WarmUpEx} />
            <Route path="/chestvids" component={ChestExVids} />
            <Route path="/backvids" component={BackExVids} />
            <Route path="/shouldervids" component={ShoulderExVids} />
            <Route path="/bicepvids" component={BicepExVids} />
            <Route path="/tricepvids" component={TricepExVids} />
            <Route path="/legvids" component={LegExVids} />
            <Route path="/corevids" component={CoreExVids} />
            <Route path="/stretchvids" component={StretchExVids} />
            <Route path="/warmupvids" component={WarmUpExVids} />
            <Route path="/mod" component={BoardModerator} />
            <Route path="/admin" component={BoardAdmin} />
          </Switch>
        </div>
      </div>
    );
  }
}

export default App;
